package com.wytiger.common.utils.common;import android.app.ProgressDialog;import android.content.Context;public class DialogUtil {	DialogUtil instance = null;	ProgressDialog progressDialog;	public DialogUtil(Context context) {		progressDialog = new ProgressDialog(context);		progressDialog.setMessage("请稍后。。");		progressDialog.setProgressStyle(ProgressDialog.STYLE_SPINNER);		progressDialog.setCancelable(false);	}	public DialogUtil getInstance(Context context) {		if (instance == null) {			instance = new DialogUtil(context);		}		return instance;	}	public void showDialog() {		progressDialog.show();	}	public void closeDialog() {		progressDialog.dismiss();	}}